<script lang="ts" setup>
import { ref, watch } from 'vue'
import { useRoute } from 'vue-router'
const route = useRoute()
import {
  Dialog,
  DialogPanel,
  TransitionChild,
  TransitionRoot,
} from '@headlessui/vue'
import {
  Bars3Icon,
  ChartBarIcon,
  CircleStackIcon,
  SignalIcon,
  XMarkIcon,
  ShieldCheckIcon,
} from '@heroicons/vue/24/outline'
import { upperCase } from 'lodash'

const navigation = ref([
  { name: '概述', href: '/home', icon: ChartBarIcon, current: true },
  { name: '控制台', href: '/dashBoard', icon: SignalIcon, current: false },
  { name: '测试', href: '/scrapyTest', icon: ShieldCheckIcon, current: false },
])
watch(route, to => {
  const result = to.fullPath.split('/').slice(0, 2).join('/')
  navigation.value.forEach(item => {
    item.current = item.href === result
  })
  systemSetting.value.forEach(item => {
    item.current = item.href === result
  })
})
const systemSetting = ref([
  { name: '监控', href: '/systemWatch', icon: CircleStackIcon, current: false },
])
const sidebarOpen = ref(false)
</script>

